package m1.moduleProjet.traitement;

public class Additionneur {
	
	private float operande1, operande2;

	public Additionneur(float op1,float op2) {
		// TODO Auto-generated constructor stub
		this.operande1 = op1;
		this.operande2 = op2;
	}
	
	public float addition(){
		
		return operande1+operande2;	
	}

}
